package net.swofty.type.village.npcs;

import net.minestom.server.coordinate.Pos;
import net.swofty.type.village.gui.GUISeymour;
import net.swofty.types.generic.entity.npc.NPCDialogue;
import net.swofty.types.generic.entity.npc.NPCParameters;

public class NPCSeymour extends NPCDialogue {

    public NPCSeymour() {
        super(new NPCParameters() {
            @Override
            public String[] holograms() {
                return new String[]{"§9Seymour", "§e§lCLICK"};
            }

            @Override
            public String signature() {
                return "";
            }

            @Override
            public String texture() {
                return "";
            }

            @Override
            public Pos position() {
                return new Pos(25, 65, -41, 112, 0);
            }

            @Override
            public boolean looking() {
                return true;
            }
        });
    }

    @Override
    public void onClick(PlayerClickNPCEvent e) {
        if (isInDialogue(e.player())) return;

        setDialogue(e.player(), "open-shop").thenRun(() -> new GUISeymour().open(e.player()));
    }

    @Override
    public DialogueSet[] getDialogueSets() {
        return new DialogueSet[] {
                NPCDialogue.DialogueSet.builder()
                        .key("open-shop").lines(new String[]{
                                "Looking to buy something fancy?",
                        }).build(),
        };
    }
}
